import { EventEmitter, Inject, Injectable } from "@angular/core";
import { Recipe } from "./recipe.model";
import { Ingredient } from "../shared/ingredient.model";
import { ShoppingListService } from "../shopping-list/shopping-list.service";
import { Subject } from "rxjs";

@Injectable()
export class ReceipeService {

    constructor(private slservice: ShoppingListService) {}
    receipeSelected = new Subject<Recipe>();
    receipeChanged = new Subject<Recipe[]>();
    private recipes: Recipe[] = [
        new Recipe('Sandwitch', 'This is simply a test', 'data:image/jpeg;base64,', 
        [ new Ingredient("Meat", 1),
            new Ingredient("French fries", 50)]),
        new Recipe('Burger', 'This is simply a test', 'data:image/jpeg;base64,',
        [ new Ingredient("Buns", 1),
        new Ingredient("Meat", 50)]),
    ];

    getReceipes() {
        return this.recipes.slice();
    }

    getReceipeByIndex(index: number) {
        return this.recipes[index];
    }

    addIngredient(ingredient: Ingredient[]) {
        this.slservice.addMultipleIngredient(ingredient);
    }

    addReceipe(receipe: Recipe) {
        this.recipes.push(receipe);
        this.receipeChanged.next(this.recipes.slice());
    }

    updateReceipe(index: number, receipe: Recipe) {
        this.recipes[index] = receipe;
        this.receipeChanged.next(this.recipes.slice());
    }

    deleteReceipe(index: number) {
        this.recipes.splice(index, 1);
        this.receipeChanged.next(this.recipes.slice());
    }

    setReceipe(receipe: Recipe[]) {
        this.recipes = receipe;
        this.receipeChanged.next(this.recipes.slice());
    }
}